mapper.update:
update comment
set username = #{username,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  subject = #{subject,jdbcType=VARCHAR},
  message = #{message,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, email, subject, message
from comment
where id = #{id,jdbcType=INTEGER}
